mapper.select:
SELECT
	USER_ID ,
	USER_NM ,
	EMAIL
FROM
	GS_USER_M
WHERE
	USER_NM = #{userNm}
AND MOBILE = #{tel}
AND EMAIL = #{email}


SELECT 
	COUNT(USER_ID)
FROM
	GS_USER_M
WHERE
	USER_NM = #{userNm}
AND MOBILE = #{tel}
AND EMAIL = #{email}

mapper.update:
UPDATE GS_USER_M
	SET
		PW = SCP.ENC_B64('KEY1','s12341234')
	WHERE
		USER_NM = #{userNm}
	AND MOBILE = #{tel}
