sqlMap.select:

			SELECT 
 			(
 				SELECT COUNT(*) FROM gs_ealy_cns_doc WHERE cnsr_id = #schCnsrId# and DATE_FORMAT(db_ins_tm,'%Y%m') = DATE_FORMAT(NOW(),'%Y%m')
 			) AS 'C1',
			 (
			 	SELECT COUNT(*) FROM gs_ealy_cns_doc WHERE cnsr_id = #schCnsrId# and CNS_METHD = '1' and DATE_FORMAT(db_ins_tm,'%Y%m') = DATE_FORMAT(NOW(),'%Y%m')
			 ) AS 'C2',
			 (
			 	SELECT COUNT(*) FROM gs_ealy_cns_doc WHERE cnsr_id = #schCnsrId# and CNS_METHD = '2' and DATE_FORMAT(db_ins_tm,'%Y%m') = DATE_FORMAT(NOW(),'%Y%m')
			 ) AS 'C3',
			 (
			 	SELECT COUNT(*) FROM gs_ealy_cns_doc WHERE cnsr_id = #schCnsrId# and CNS_METHD = '3' and DATE_FORMAT(db_ins_tm,'%Y%m') = DATE_FORMAT(NOW(),'%Y%m')
			 ) AS 'C4',
			 (
			 	SELECT (select COUNT(*) FROM gs_ealy_cns_doc WHERE cnsr_id = #schCnsrId# and CNS_METHD = '4' and DATE_FORMAT(db_ins_tm,'%Y%m') = DATE_FORMAT(NOW(),'%Y%m'))+
			 			 (select COUNT(*) FROM gs_ealy_cns_doc WHERE cnsr_id = #schCnsrId# and CNS_METHD = '5' and DATE_FORMAT(db_ins_tm,'%Y%m') = DATE_FORMAT(NOW(),'%Y%m'))
			 ) AS 'C5',
			 (
			 	select COUNT(*) FROM gs_ealy_cns_doc WHERE cnsr_id = #schCnsrId# and CNS_METHD = '6' and DATE_FORMAT(db_ins_tm,'%Y%m') = DATE_FORMAT(NOW(),'%Y%m')
			 ) AS 'C6' ,
			 (
			 	SELECT COUNT(distinct cnsle_id) FROM gs_per_cns_dtl WHERE cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND cnsr_id = #schCnsrId# and DATE_FORMAT(db_ins_tm,'%Y%m') = DATE_FORMAT(NOW(),'%Y%m')
			 ) AS 'C7' ,
			 (
			 	SELECT COUNT(*) FROM gs_per_cns_dtl WHERE cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND cnsr_id = #schCnsrId# and DATE_FORMAT(db_ins_tm,'%Y%m') = DATE_FORMAT(NOW(),'%Y%m')
			 ) AS 'C8' ,
			 (
			 	SELECT COUNT(*) FROM gs_per_cns_dtl WHERE cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND CNS_STAT = '1' AND cnsr_id = #schCnsrId# and DATE_FORMAT(db_ins_tm,'%Y%m') = DATE_FORMAT(NOW(),'%Y%m')
			 ) AS 'C9' ,
			 (
			 	SELECT COUNT(*) FROM gs_per_cns_dtl WHERE cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND CNS_STAT = '2' AND cnsr_id = #schCnsrId# and DATE_FORMAT(db_ins_tm,'%Y%m') = DATE_FORMAT(NOW(),'%Y%m')
			 ) AS 'C10' ,
			 (
			 	SELECT COUNT(*) FROM gs_per_cns_dtl WHERE cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND CNS_STAT = '3' AND cnsr_id = #schCnsrId# and DATE_FORMAT(db_ins_tm,'%Y%m') = DATE_FORMAT(NOW(),'%Y%m')
			 ) AS 'C11' ,
			 (
			 	SELECT COUNT(*) FROM gs_per_cns_dtl WHERE cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND CNS_STAT = '4' AND cnsr_id = #schCnsrId# and DATE_FORMAT(db_ins_tm,'%Y%m') = DATE_FORMAT(NOW(),'%Y%m')
			 ) AS 'C12' ,
			 (
			 	SELECT COUNT(*) FROM gs_per_cns_dtl WHERE cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND CNS_STAT = '5' AND cnsr_id = #schCnsrId# and DATE_FORMAT(db_ins_tm,'%Y%m') = DATE_FORMAT(NOW(),'%Y%m')
			 ) AS 'C13'

SELECT ( SELECT center_nm FROM gs_center_m WHERE num = #centerGb# ) AS 'C0', ( select count(*) from gs_ealy_cns_doc WHERE center_gb = #centerGb# ) AS 'C1', ( select (select count(*) from gs_per_cns_dtl WHERE cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND center_gb = #centerGb# ) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl WHERE cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# ) ) as 'C2', ( select count(*) from gs_ealy_cns_doc where center_gb = #centerGb# ) AS 'C3', ( select (select count(*) from gs_per_cns_dtl WHERE cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND center_gb = #centerGb# ) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl WHERE cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# ) ) as 'C4', ( select count(*) from GS_EALY_CNS_DOC where GENDER = 'M' AND center_gb = #centerGb# ) as 'C5', ( select (select count(*) from gs_per_cns_dtl WHERE cns_dtl_gb_cd = '1' AND GENDER = 'M' AND use_yn = 'Y' AND center_gb = #centerGb# ) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl WHERE cns_dtl_gb_cd = '2' AND GENDER = 'M' AND use_yn = 'Y' AND center_gb = #centerGb# )
) as 'C6', ( select count(*) from GS_EALY_CNS_DOC where GENDER = 'F' AND center_gb = #centerGb# ) as 'C7', ( select (select count(*) from gs_per_cns_dtl WHERE cns_dtl_gb_cd = '1' AND GENDER = 'F' AND use_yn = 'Y' AND center_gb = #centerGb# ) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl WHERE cns_dtl_gb_cd = '2' AND GENDER = 'F' AND use_yn = 'Y' AND center_gb = #centerGb# ) ) as 'C8', ( select (select count(*) from gs_per_cns_dtl WHERE cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND center_gb = #centerGb# ) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl WHERE cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# ) ) as 'C9', ( SELECT (select count(*) from gs_per_cns_dtl where cnsle_rel = '1' AND cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND center_gb = #centerGb# ) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl where cnsle_rel = '1' AND cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# ) ) as 'C10', ( select (select count(*) from gs_per_cns_dtl WHERE cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND center_gb = #centerGb# ) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl WHERE cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# ) - (select count(*) from gs_per_cns_dtl where cnsle_rel = '1' AND cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND center_gb = #centerGb# ) - (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl where cnsle_rel = '1' AND cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# ) ) AS 'C11' , ( SELECT (select count(*) from gs_per_cns_dtl where cnsle_rel = '2' AND cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND center_gb = #centerGb# ) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl where cnsle_rel = '2' AND cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# ) ) AS 'C12', ( SELECT (select count(*) from gs_per_cns_dtl where cnsle_rel = '3' AND cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND center_gb = #centerGb# ) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl where cnsle_rel = '3' AND cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# ) ) as 'C13', ( SELECT (select count(*) from gs_per_cns_dtl where cnsle_rel = '4' AND cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND center_gb = #centerGb# ) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl where cnsle_rel = '4' AND cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# ) ) as 'C14', ( SELECT (select count(*) from gs_per_cns_dtl where cnsle_rel = '5' AND cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND center_gb = #centerGb# ) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl where cnsle_rel = '5' AND cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# ) ) as 'C15', ( SELECT (select count(*) from gs_per_cns_dtl where cnsle_rel = '6' AND cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND center_gb = #centerGb# ) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl where cnsle_rel = '6' AND cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# ) ) as 'C16', ( select count(*) from gs_ealy_cns_doc WHERE center_gb = #centerGb# ) AS 'C17', ( select (select count(*) from gs_per_cns_dtl WHERE cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND center_gb = #centerGb# ) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl WHERE cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# ) ) as 'C18', ( select count(*) from GS_EALY_CNS_DOC where FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) >= 60 and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) < 70 AND center_gb = #centerGb# ) as 'C19', ( SELECT (select count(*) from gs_per_cns_dtl where center_gb = #centerGb# and cns_dtl_gb_cd = '1' AND use_yn = 'Y' and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) >= 60 and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) < 70) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl where cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) >= 60 and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) < 70) ) as 'C20', ( select count(*) from GS_EALY_CNS_DOC where center_gb = #centerGb# and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) >= 70 and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) < 80 ) as 'C21', ( SELECT (select count(*) from gs_per_cns_dtl where cns_dtl_gb_cd = '1' AND use_yn = 'Y' and center_gb = #centerGb# and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) >= 70 and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) < 80) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl where cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) >= 70 and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) < 80)
) as 'C22', ( select count(*) from GS_EALY_CNS_DOC where center_gb = #centerGb# and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) >= 80 and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) < 90 ) as 'C23', (
SELECT (select count(*) from gs_per_cns_dtl where cns_dtl_gb_cd = '1' AND use_yn = 'Y' and center_gb = #centerGb# and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) >= 80 and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) < 90) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl where cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) >= 80 and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) < 90)
) as 'C24', ( select count(*) from GS_EALY_CNS_DOC where center_gb = #centerGb# and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) >= 90 and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) < 100 ) as 'C25', (
SELECT (select count(*) from gs_per_cns_dtl where cns_dtl_gb_cd = '1' AND use_yn = 'Y' AND center_gb = #centerGb# and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) >= 90 and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) < 100) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl where cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) >= 90 and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) < 100)
) as 'C26', ( select count(*) from GS_EALY_CNS_DOC WHERE center_gb = #centerGb# and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(REPLACE(BIRTH_DT,'.',''), '%Y%m%d'))/365) < 60 ) as 'C27', (
SELECT (select count(*) from gs_per_cns_dtl where cns_dtl_gb_cd = '1' AND use_yn = 'Y' and center_gb = #centerGb# and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) < 60) + (select TRUNCATE(IFNULL(sum(cns_cnt),0)+0 ,0) from gs_per_cns_dtl where cns_dtl_gb_cd = '2' AND use_yn = 'Y' AND center_gb = #centerGb# and FLOOR(DATEDIFF(CURRENT_DATE, STR_TO_DATE(BIRTH_DT, '%Y%m%d'))/365) < 60)
) as 'C28' ,
( select count(*) from gs_ealy_cns_doc WHERE center_gb = #centerGb# ) AS 'C29' , ( select count(*) from GS_EALY_CNS_DOC where CNS_METHD != '12' AND center_gb = #centerGb# ) as 'C30' , ( select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '1' AND center_gb = #centerGb# ) as 'C31' , ( SELECT (select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '2' AND center_gb = #centerGb#) + (select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '3' AND center_gb = #centerGb#) + (select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '4' AND center_gb = #centerGb#) + (select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '5' AND center_gb = #centerGb#) + (select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '6' AND center_gb = #centerGb#) + (select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '7' AND center_gb = #centerGb#) ) AS 'C171' , ( select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '2' AND center_gb = #centerGb# ) as 'C32' , ( select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '3' AND center_gb = #centerGb# ) as 'C33' , ( select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '4' AND center_gb = #centerGb# ) as 'C34' , ( select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '5' AND center_gb = #centerGb# ) as 'C35' , ( select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '6' AND center_gb = #centerGb# ) as 'C36' , ( select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '7' AND center_gb = #centerGb# ) as 'C37' , ( select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '8' AND center_gb = #centerGb# ) as 'C38' , ( select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '9' AND center_gb = #centerGb# ) as 'C39' , ( select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '10' AND center_gb = #centerGb# ) as 'C40' , ( select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '11' AND center_gb = #centerGb# ) as 'C41' , ( select count(*) from GS_EALY_CNS_DOC where CNS_METHD = '12' AND center_gb = #centerGb# ) as 'C42' ,
